            RuleFor(a => a.FirstName)
                .NotEmpty()
                .MinimumLength(2)
                .MaximumLength(15)
                .Matches("^[A-Z]{1}[a-z]{1,14}$")
                .WithMessage("First name must start uppercase");

            RuleFor(a => a.LastName)
                .NotEmpty()
                .MinimumLength(2)
                .MaximumLength(15)
                .Matches("^[A-Z]{1}[a-z]{1,14}$")
                .WithMessage("Last name must start uppercase");

